/**
 *
 * @category    Magecheckout
 * @package     Magecheckout_SecuredCheckout
 * @author      Magecheckout Developer
 */
class Magecheckout_SecuredCheckout_Model_Total_Pdf_Giftwrap extends Mage_Sales_Model_Order_Pdf_Total_Default
{
    /**
     * Get array of arrays with totals information for display in PDF
     * array(
     *  $index => array(
     *      'amount'   => $amount,
     *      'label'    => $label,
     *      'font_size'=> $font_size
     *  )
     * )
     *
     * @return array
     */
    public function getTotalsForDisplay()
    {
        if (!$this->getAmount())
            return $this;
        $amount = $this->getOrder()->formatPriceTxt($this->getAmount());
        if ($this->getAmountPrefix()) {
            $amount = $this->getAmountPrefix() . $amount;
        }
        $label    = Mage::helper('securedcheckout')->__($this->getTitle()). ':';
        $fontSize = $this->getFontSize() ? $this->getFontSize() : 7;
        $total    = array(
            'amount'    => $amount,
            'label'     => $label,
            'font_size' => $fontSize
        );

        return array($total);
    }

    /**
     * Get Total amount from source
     *
     * @return float
     */
    public function getAmount()
    {
        return $this->getSource()->getMcGiftwrapAmount();
    }
}
